import React, { useState } from 'react';
import {
  Container,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

function SearchForm() {
  const [keyword, setKeyword] = useState('land registry searches');
  const [url, setUrl] = useState('infotrack.co.uk');
  const [searchEngine, setSearchEngine] = useState('Google');
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [results, setResults] = useState([]);

  const toggleDropdown = () => setDropdownOpen((prevState) => !prevState);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Simulated API call with form data
    const apiResponse = await mockApiCall({ keyword, url, searchEngine });

    // Set the results state with the API response
    setResults(apiResponse);
  };

  // Mock API call function
  const mockApiCall = ({ keyword, url, searchEngine }) => {
    console.log('API called with:', keyword, url, searchEngine);
    // Simulate API response with a list of integers
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve([1, 3, 7, 10]);
      }, 1000);
    });
  };

  return (
    <Container>
      <h2>Search Form</h2>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="keyword">Keyword</Label>
          <Input
            type="text"
            id="keyword"
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="url">URL</Label>
          <Input
            type="text"
            id="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="searchEngine">Search Engine</Label>
          <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
            <DropdownToggle caret>{searchEngine}</DropdownToggle>
            <DropdownMenu>
              <DropdownItem onClick={() => setSearchEngine('Google')}>
                Google
              </DropdownItem>
              <DropdownItem onClick={() => setSearchEngine('Bing')}>
                Bing
              </DropdownItem>
            </DropdownMenu>
          </Dropdown>
        </FormGroup>
        <Button type="submit" color="primary">
          Submit
        </Button>
      </Form>

      <h3>Results</h3>
      {results.length > 0 ? (
        <ul>
          {results.map((result, index) => (
            <li key={index}>Position: {result}</li>
          ))}
        </ul>
      ) : (
        <p>No results to display</p>
      )}
    </Container>
  );
}

export default SearchForm;
