import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // Add this import for date handling
import { Container, Row, Col, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Table } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import Navbar from "../component/Navbar/SimpleNavBar.js";

// Registering Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale // Register the time scale
);

function Records() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [selectedEngine, setSelectedEngine] = useState('All');
  const [data, setData] = useState({
    labels: [],
    datasets: []
  });

  const [tableData, setTableData] = useState([]);
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' or 'desc'

  useEffect(() => {
    fetch('http://localhost:5205/history') // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => {
        // Filter and process the data
        const filteredData = data
          .filter(item => item.Keyword === 'land registry searches' && 
                          item.URL === 'infotrack.co.uk' && 
                          item.Display === 1 &&
                          (selectedEngine === 'All' || item.SearchEngine === selectedEngine))
          .map(item => {
            const date = new Date(item.CreatedDate);
            const position = item.Positions.split(',').map(Number).filter(num => !isNaN(num));
            return {
              ...item,
              CreatedDate: isNaN(date.getTime()) ? null : date.toISOString(), // Handle invalid dates
              Position: position.length ? Math.min(...position) : null
            };
          })
          .filter(item => item.CreatedDate !== null && item.Position !== null); // Filter out any invalid dates or positions

        const sortedData = filteredData.sort((a, b) => new Date(b.CreatedDate) - new Date(a.CreatedDate));
        setTableData(sortedData);

        const labels = sortedData.map(item => new Date(item.CreatedDate));
        const positions = sortedData.map(item => item.Position);

        // Update the chart data
        setData({
          labels: labels,
          datasets: [{
            label: selectedEngine,
            data: positions,
            borderColor: 'rgba(75,192,192,1)',
            backgroundColor: 'rgba(75,192,192,0.2)',
            fill: true,
          }]
        });
      })
      .catch(error => console.error('Error fetching data:', error));
  }, [selectedEngine]); // Dependency on selectedEngine

  // Toggle dropdown visibility
  const toggleDropdown = () => setDropdownOpen(prevState => !prevState);

  // Filter data based on selected engine
  const filteredData = () => {
    return data;
  };

  // Sort table data
  const sortDataByDate = () => {
    const sortedData = [...tableData].sort((a, b) => {
      const dateA = new Date(a.CreatedDate);
      const dateB = new Date(b.CreatedDate);
      return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
    });
    setTableData(sortedData);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  // Format date for display
  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return isNaN(date.getTime()) ? 'Invalid Date' : date.toLocaleString(); // Handle invalid dates
  };

  return (
    <div className="App">
      <Navbar />
      <div className="App-header">
        <Container>
          <Row>
            <Col md={12}>
              <h2>Ranking Trend</h2>
              <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
                <DropdownToggle caret>
                  {selectedEngine}
                </DropdownToggle>
                <DropdownMenu>
                  <DropdownItem onClick={() => setSelectedEngine('All')}>Show All</DropdownItem>
                  <DropdownItem onClick={() => setSelectedEngine('Google')}>Google</DropdownItem>
                  <DropdownItem onClick={() => setSelectedEngine('Bing')}>Bing</DropdownItem>
                </DropdownMenu>
              </Dropdown>
              <Line
                data={filteredData()}
                options={{
                  responsive: true,
                  plugins: {
                    legend: {
                      position: 'top',
                    },
                    tooltip: {
                      callbacks: {
                        label: function (context) {
                          return `Position: ${context.raw}`;
                        },
                      },
                    },
                  },
                  scales: {
                    x: {
                      type: 'time',
                      time: {
                        unit: 'day',
                        tooltipFormat: 'll',
                      },
                      title: {
                        display: true,
                        text: 'Date',
                      },
                      ticks: {
                        autoSkip: true,
                        maxTicksLimit: 10, // Adjust this to avoid clutter
                      },
                    },
                    y: {
                      title: {
                        display: true,
                        text: 'Rank',
                      },
                      suggestedMin: 1,
                      suggestedMax: 100,
                      reverse: true, // Flip the y-axis
                    },
                  },
                }}
              />
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              <h3>History Records</h3>
              <Table striped>
                <thead>
                  <tr>
                    <th onClick={sortDataByDate} style={{ cursor: 'pointer' }}>
                      Date {sortOrder === 'asc' ? 'ðŸ”¼' : 'ðŸ”½'}
                    </th>
                    <th>Keyword</th>
                    <th>URL</th>
                    <th>Search Engine</th>
                    <th>Positions</th>
                  </tr>
                </thead>
                <tbody>
                  {tableData.map((record, index) => (
                    <tr key={index}>
                      <td>{formatDate(record.CreatedDate)}</td>
                      <td>{record.Keyword}</td>
                      <td>{record.Url}</td>
                      <td>{record.SearchEngine}</td>
                      <td>{record.Position}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Records;
