import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Container, Row, Col, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

// Registering Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function Record() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [selectedEngine, setSelectedEngine] = useState('All');
  const [data, setData] = useState({
    labels: ['2024-08-01', '2024-08-02', '2024-08-03', '2024-08-04'],
    datasets: [
      {
        label: 'Google',
        data: [30, 40, 35, 50],
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: true,
      },
      {
        label: 'Bing',
        data: [25, 35, 30, 45],
        borderColor: 'rgba(153,102,255,1)',
        backgroundColor: 'rgba(153,102,255,0.2)',
        fill: true,
      },
    ],
  });

  // Toggle dropdown visibility
  const toggleDropdown = () => setDropdownOpen(prevState => !prevState);

  // Filter data based on selected engine
  const filteredData = () => {
    if (selectedEngine === 'All') {
      return data;
    }

    return {
      labels: data.labels,
      datasets: data.datasets.filter(dataset => dataset.label === selectedEngine),
    };
  };

  return (
    <Container>
      <Row>
        <Col md={12}>
          <h2>Ranking Trend</h2>
          <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
            <DropdownToggle caret>
              {selectedEngine}
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem onClick={() => setSelectedEngine('All')}>Show All</DropdownItem>
              <DropdownItem onClick={() => setSelectedEngine('Google')}>Google</DropdownItem>
              <DropdownItem onClick={() => setSelectedEngine('Bing')}>Bing</DropdownItem>
            </DropdownMenu>
          </Dropdown>
          <Line
            data={filteredData()}
            options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
                tooltip: {
                  callbacks: {
                    label: function (context) {
                      return `Position: ${context.raw}`;
                    },
                  },
                },
              },
              scales: {
                x: {
                  title: {
                    display: true,
                    text: 'Date',
                  },
                },
                y: {
                  title: {
                    display: true,
                    text: 'Rank',
                  },
                  suggestedMin: 1,
                  suggestedMax: 100,
                },
              },
            }}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default Record;
