import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Dropdown, DropdownButton, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Sample data for demonstration
const sampleData = {
  google: [10, 20, 30, 25, 15, 5, 1],
  bing: [50, 45, 40, 35, 30, 25, 20],
  labels: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5', 'Day 6', 'Day 7'],
};

const Records = () => {
  const [selectedEngine, setSelectedEngine] = useState('All');

  const handleSelect = (eventKey) => {
    setSelectedEngine(eventKey);
  };

  // Generate the dataset based on the selected search engine
  const generateDataset = () => {
    const datasets = [];

    if (selectedEngine === 'All' || selectedEngine === 'Google') {
      datasets.push({
        label: 'Google',
        data: sampleData.google,
        fill: false,
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(75,192,192,1)',
      });
    }

    if (selectedEngine === 'All' || selectedEngine === 'Bing') {
      datasets.push({
        label: 'Bing',
        data: sampleData.bing,
        fill: false,
        backgroundColor: 'rgba(153,102,255,1)',
        borderColor: 'rgba(153,102,255,1)',
      });
    }

    return datasets;
  };

  const chartData = {
    labels: sampleData.labels,
    datasets: generateDataset(),
  };

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h2>Ranking Trend Graph</h2>
        </Col>
        <Col className="text-right">
          <DropdownButton
            id="dropdown-basic-button"
            title={`Showing: ${selectedEngine}`}
            onSelect={handleSelect}
          >
            <Dropdown.Item eventKey="All">All</Dropdown.Item>
            <Dropdown.Item eventKey="Google">Google</Dropdown.Item>
            <Dropdown.Item eventKey="Bing">Bing</Dropdown.Item>
          </DropdownButton>
        </Col>
      </Row>
      <Row>
        <Col>
          <Line
            data={chartData}
            options={{
              scales: {
                y: {
                  min: 1,
                  max: 100,
                  reverse: true,
                  ticks: {
                    stepSize: 10,
                  },
                },
              },
            }}
          />
        </Col>
      </Row>
    </Container>
  );
};

export default Records;
